const userPref=window.matchMedia("(prefers-color-scheme: light)").matches?"light":"dark",currentTheme=localStorage.getItem("theme")??userPref;function sendMessage(t){const e=document.querySelector<HTMLIFrameElement>"iframe.giscus-frame";if(!e)return;e.contentWindow.postMessage({giscus:t},"https://giscus.app")}currentTheme&&document.documentElement.setAttribute("saved-theme",currentTheme);const switchTheme=e=>{e.target.checked?(document.documentElement.setAttribute("saved-theme","dark"),localStorage.setItem("theme","dark"),sendMessage({theme:"https://giscus.app/themes/dark.css"})):(document.documentElement.setAttribute("saved-theme","light"),localStorage.setItem("theme","light"),sendMessage({theme:"https://giscus.app/themes/light.css"}))};window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("#darkmode-toggle");e.addEventListener("change",switchTheme,!1),currentTheme==="dark"&&(e.checked=!0)})