const userPref=window.matchMedia("(prefers-color-scheme: light)").matches?"light":"dark",currentTheme=localStorage.getItem("theme")??userPref;function giscusThemeChange(t){const e=document.querySelector<HTMLIFrameElement>"iframe.giscus-frame";if(!e)return;e.contentWindow.sendMessage({setConfig:{theme:t}})}currentTheme&&document.documentElement.setAttribute("saved-theme",currentTheme);const switchTheme=e=>{e.target.checked?(document.documentElement.setAttribute("saved-theme","dark"),localStorage.setItem("theme","dark"),giscusThemeChange("dark")):(document.documentElement.setAttribute("saved-theme","light"),localStorage.setItem("theme","light"),giscusThemeChange("light"))};window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("#darkmode-toggle");e.addEventListener("change",switchTheme,!1),currentTheme==="dark"&&(e.checked=!0)})